CREATE FUNCTION         "ID_TO_TAG" (p_id number)
return varchar
as
  v_id number := p_id;
  v_mod number;
  v_tag_char char;
  v_digits constant varchar2(36) := '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  v_tag varchar2(50) := '';
  
begin

  if p_id = 0 then
    v_tag := '0';
  end if;
  
  while v_id != 0 loop
    v_mod := v_id mod 36;
    v_tag_char := substr(v_digits, v_mod + 1, 1);
    v_tag := v_tag_char || v_tag;
    v_id := floor(v_id / 36);
  end loop;
  
  return v_tag;

end;
/
 
CREATE FUNCTION         "FM_GET_FOLDER_FULL_NAME" (
  p_folder_id number
