    VALUES(
      jobStepID,
      tableName,
      dmlType,
      expRecordCnt);
    COMMIT;

  end if; 
  
     dbms_output.put_line(seqID ||'A' || jobStepID ||'B' || jobID);
  
--insert a record into the job step run table
  INSERT INTO 
    AZ_JOB_STEP_RUN (
      JOB_RUN_ID, 
      JOB_STEP_ID, 
      START_DATE, 
      STATUS,
      SEQ_ID) 
  VALUES(
    jobRunID, 
    jobStepID, 
    insertDt, 
    'RUNNING',
    seqID);
   COMMIT;    
    
  --Get Job step Run ID
