		  ,rbm.timepoint;
	  
	stepCt := stepCt + 1;
    control.cz_write_audit(jobId,databaseName,procedureName,'Inserted trial into DEAPP_WZ de_subject_rbm_data',SQL%ROWCOUNT,stepCt,'Done');
	commit;

	execute immediate('drop index deapp_wz.stg_subject_rbm_data_i1');
