  SET CATEGORY_PATH = REPLACE(CATEGORY_PATH, 'Elisa', 'ELISA')
  where category_path like '%Elisa%';
  commit;
  
  --Clean up LZ tables
  delete  
    from i2b2_lz.stg_category;
  
  delete 
    from i2b2_lz.time_point_measurement
      where study_id = trialID;
  commit;
END;

 
 
 
 
 
 
