* See the License for the specific language governing permissions and
* limitations under the License.
******************************************************************/

	runType	varchar2(100);
	idxExists	number;
	pExists	number;
	tableName	varchar2(50);
	sqlText	varchar2(500);

	type index_names_rec is record
	(index_name		varchar2(100)
	,owner			varchar2(100)
	);
	
	type index_names is table of index_names_rec;
	index_name_array index_names;

	--Audit variables
	newJobFlag INTEGER(1);
	databaseName VARCHAR(100);
	procedureName VARCHAR(100);
	jobID number(18,0);
	stepCt number(18,0);

BEGIN

	runType := upper(run_type);
	
	--Set Audit Parameters
	newJobFlag := 0; -- False (Default)
	jobID := currentJobID;

	SELECT sys_context('USERENV', 'CURRENT_SCHEMA') INTO databaseName FROM dual;
	procedureName := $$PLSQL_UNIT;

	--Audit JOB Initialization
	--If Job ID does not exist, then this is a single procedure run and we need to create it
	IF(jobID IS NULL or jobID < 1)
	THEN
		newJobFlag := 1; -- True
		czx_start_audit (procedureName, databaseName, jobID);
	END IF;
    	
	stepCt := 0;
	
	stepCt := stepCt + 1;
	czx_write_audit(jobId,databaseName,procedureName,'Start observation_fact index '|| run_type ,0,stepCt,'Done');
	
	if runType not in ('DROP','ADD') then
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Invalid run_type: ' || run_type,0,stepCt,'Done');
	else
		if runType = 'DROP' then
			select index_name, owner
			bulk collect into index_name_array
			from all_indexes
			where table_name = 'OBSERVATION_FACT';

			if index_name_array.count = 0 then
				stepCt := stepCt + 1;
				czx_write_audit(jobId,databaseName,procedureName,'No indexes found for observation_fact',0,stepCt,'Done');
			else
				for i in index_name_array.first .. index_name_array.last
				loop
					sqlText := 'drop index ' || index_name_array(i).owner || '.' || index_name_array(i).index_name;
					stepCt := stepCt + 1;
					czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
					execute immediate(sqlText);
					stepCt := stepCt + 1;
					czx_write_audit(jobId,databaseName,procedureName,'Drop complete',0,stepCt,'Done');
				end loop;
			end if;
		else
			--	create the indexes
			sqlText := 'CREATE UNIQUE INDEX "I2B2DEMODATA"."OB_FACT_PK" ON "I2B2DEMODATA"."OBSERVATION_FACT" ("ENCOUNTER_NUM", "PATIENT_NUM", "CONCEPT_CD", "PROVIDER_ID", "START_DATE", "MODIFIER_CD", "INSTANCE_NUM")';
			sqlText := sqlText || ' NOLOGGING COMPUTE STATISTICS TABLESPACE "INDX"';
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
			execute immediate(sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Create complete',0,stepCt,'Done');
			
			sqlText := 'CREATE INDEX "I2B2DEMODATA"."IDX_OB_FACT_2" ON "I2B2DEMODATA"."OBSERVATION_FACT" ("CONCEPT_CD", "PATIENT_NUM", "ENCOUNTER_NUM")';
			sqlText := sqlText || ' NOLOGGING COMPUTE STATISTICS COMPRESS 1 TABLESPACE "INDX"';
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
			execute immediate(sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Create complete',0,stepCt,'Done');
			
			sqlText := 'CREATE INDEX "I2B2DEMODATA"."IDX_OB_FACT_1" ON "I2B2DEMODATA"."OBSERVATION_FACT" ("CONCEPT_CD")';
			sqlText := sqlText || ' NOLOGGING COMPUTE STATISTICS COMPRESS 1 TABLESPACE "INDX"';
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
			execute immediate(sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Create complete',0,stepCt,'Done');
			
			sqlText := 'CREATE INDEX "I2B2DEMODATA"."OF_CTX_BLOB" ON "I2B2DEMODATA"."OBSERVATION_FACT" ("OBSERVATION_BLOB")';
			sqlText := sqlText || ' INDEXTYPE IS "CTXSYS"."CONTEXT"  PARAMETERS (' || '''' || 'SYNC (on commit)' || '''' || ')';
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
			execute immediate(sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Create complete',0,stepCt,'Done');
			
			sqlText := 'CREATE INDEX "I2B2DEMODATA"."OBS_FACT_MODIFIER" ON "I2B2DEMODATA"."OBSERVATION_FACT" ("MODIFIER_CD") ';
			sqlText := sqlText || ' NOLOGGING COMPUTE STATISTICS TABLESPACE "INDX"';
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
			execute immediate(sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Create complete',0,stepCt,'Done');
			
			sqlText := 'CREATE INDEX "I2B2DEMODATA"."OBS_FACT_ENC" ON "I2B2DEMODATA"."OBSERVATION_FACT" ("ENCOUNTER_NUM") ';
			sqlText := sqlText || ' NOLOGGING COMPUTE STATISTICS TABLESPACE "INDX"';			
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,sqlText,0,stepCt,'Done');
			execute immediate(sqlText);
			stepCt := stepCt + 1;
