  END IF;
    	
  stepCt := 0;

	stepCt := stepCt + 1;
	czx_write_audit(jobId,databaseName,procedureName,'Starting zscore calc for ' || TrialId || ' RunType: ' || runType || ' dataType: ' || dataType,0,stepCt,'Done');

