CREATE PROCEDURE "I2B2_MOVE_ANALYSIS_TO_PROD" 
(i_etl_id		number	:= -1
,i_job_id		number	:= null
) AUTHID CURRENT_USER
AS
/*************************************************************************
* Copyright 2008-2012 Janssen Research & Development, LLC.
*
* Licensed under the Apache License, Version 2.0 (the "License");
* you may not use this file except in compliance with the License.
* You may obtain a copy of the License at
*
* http://www.apache.org/licenses/LICENSE-2.0
*
* Unless required by applicable law or agreed to in writing, software
* distributed under the License is distributed on an "AS IS" BASIS,
* WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
* See the License for the specific language governing permissions and
* limitations under the License.
******************************************************************/
  
    --Audit variables
	newJobFlag 	int;
	databaseName 	VARCHAR2(100);
	procedureName VARCHAR2(100);
	jobID 		number(18,0);
	stepCt 		number(18,0);
	
	v_etl_id					number(18,0);
	v_bio_assay_analysis_id		number(18,0);
	v_data_type					varchar2(50);
	v_sqlText					varchar2(2000);
	v_exists					int;
	v_GWAS_staged				int;
	v_EQTL_staged				int;
	
	type stage_rec  is record
	(bio_assay_analysis_id		number(18,0)
	,etl_id						number(18,0)
	,study_id					varchar2(500)
	,data_type					varchar2(50)
	,orig_data_type				varchar2(50)
	,analysis_name				varchar2(1000)
	);

	type stage_table is table of stage_rec; 
	stage_array stage_table;
	
	type stage_table_names_rec is record
	(table_name					varchar2(500)
	);
	
	type stage_table_names is table of stage_table_names_rec;
	stage_table_array stage_table_names;
	
	no_staged_data	exception;
	
	BEGIN	
	
	--Set Audit Parameters
	newJobFlag := 0; -- False (Default)
	jobID := -1;

	SELECT sys_context('USERENV', 'CURRENT_SCHEMA') INTO databaseName FROM dual;
	procedureName := $$PLSQL_UNIT;

	--Audit JOB Initialization
	--If Job ID does not exist, then this is a single procedure run and we need to create it
	IF(jobID IS NULL or jobID < 1)
	THEN
		newJobFlag := 1; -- True
		czx_start_audit (procedureName, databaseName, jobID);
	END IF;
    	
	stepCt := 1;	
	czx_write_audit(jobId,databaseName,procedureName,'Starting i2b2_move_analysis_to_prod',0,stepCt,'Done');
	
	--	load staged analysis to array
	
	select baa.bio_assay_analysis_id
		  ,lz.etl_id
		  ,lz.study_id
		  ,case when lz.data_type = 'Metabolic GWAS' then 'GWAS' else lz.data_type end as data_type
		  ,lz.data_type as orig_data_type
		  ,lz.analysis_name
	bulk collect into stage_array
	from tm_lz.lz_src_analysis_metadata lz
		,biomart.bio_assay_analysis baa
	where lz.status = 'STAGED'
	  and lz.study_id = baa.etl_id
	  and lz.etl_id = baa.etl_id_source
	  and case when i_etl_id = -1 then 1
			   when lz.etl_id = i_etl_id then 1
			   else 0 end = 1;
			   
	v_exists := SQL%ROWCOUNT;
	
	if v_exists = 0 then
		raise no_staged_data;
	end if;

	--	set variables if staged data contains GWAS and/or EQTL data
	
	v_GWAS_staged := 0;
	v_EQTL_staged := 0;
	
	for i in stage_array.first .. stage_array.last
	loop	
		if stage_array(i).data_type = 'GWAS' then
			v_GWAS_staged := 1;
		end if;
		
		if stage_array(i).data_type = 'EQTL' then
			v_EQTL_staged := 1;
		end if;	
		
	end loop;
	
	--	drop indexes if loading GWAS data
	
	if v_GWAS_staged = 1 then
		select count(*) into v_exists
		from all_indexes
		where owner = 'BIOMART'
		  and table_name = 'BIO_ASSAY_ANALYSIS_GWAS'
		  and index_name = 'BIO_ASSAY_ANALYSIS_GWAS_PK';
		  
		if v_exists > 0 then
			execute immediate('drop index biomart.bio_assay_analysis_gwas_pk');
		end if;

		select count(*) into v_exists
		from all_indexes
		where owner = 'BIOMART'
		  and table_name = 'BIO_ASSAY_ANALYSIS_GWAS'
		  and index_name = 'BIO_ASSAY_ANALYSIS_GWAS_IDX2';
		  
		if v_exists > 0 then
			execute immediate('drop index biomart.bio_assay_analysis_gwas_idx2');
		end if;		
	end if;

	--	delete any existing data in bio_assay_analysis_gwas and bio_assay_analysis_eqtl
			   
	if v_GWAS_staged = 1 then
		delete from biomart.bio_assay_analysis_gwas g
		where g.bio_assay_analysis_id in
			 (select x.bio_assay_analysis_id
			  from tm_lz.lz_src_analysis_metadata t
				  ,biomart.bio_assay_analysis x
			  where t.status = 'STAGED'
				and t.data_type in ('GWAS','Metabolic GWAS')
				and t.study_id = x.etl_id
				and t.etl_id = x.etl_id_source
				and case when i_etl_id = -1 then 1
						 when t.etl_id = i_etl_id then 1
						 else 0 end = 1);
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Delete exising data for staged analyses from BIOMART.BIO_ASSAY_ANALYSIS_GWAS',SQL%ROWCOUNT,stepCt,'Done');
		commit;	
	end if;

	if v_EQTL_staged = 1 then
		delete from biomart.bio_assay_analysis_eqtl g
		where g.bio_assay_analysis_id in
			 (select x.bio_assay_analysis_id
			  from tm_lz.lz_src_analysis_metadata t
				  ,biomart.bio_assay_analysis x
			  where t.status = 'STAGED'
				and t.data_type = 'EQTL'
				and t.study_id = x.etl_id
				and t.etl_id = x.etl_id_source
				and case when i_etl_id = -1 then 1
						 when t.etl_id = i_etl_id then 1
						 else 0 end = 1);
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Delete exising data for staged analyses from BIOMART.BIO_ASSAY_ANALYSIS_EQTL',SQL%ROWCOUNT,stepCt,'Done');
		commit;	
	end if;
	
	if v_GWAS_staged = 1 then
		select count(*) into v_exists
		from all_indexes
		where owner = 'BIOMART'
		  and table_name = 'BIO_ASSAY_ANALYSIS_GWAS'
		  and index_name = 'BIO_ASSAY_ANALYSIS_GWAS_IDX1';
		  
		if v_exists > 0 then
			execute immediate('drop index biomart.BIO_ASSAY_ANALYSIS_GWAS_IDX1');
		end if;	
	end if;
	
	for i in stage_array.first .. stage_array.last
	loop
		
		czx_write_audit(jobId,databaseName,procedureName,'Loading ' || stage_array(i).study_id || ' ' || stage_array(i).orig_data_type || ' ' ||
					   stage_array(i).analysis_name,0,stepCt,'Done');
					   
		v_etl_id := stage_array(i).etl_id;
		v_bio_assay_analysis_id := stage_array(i).bio_assay_analysis_id;
		v_data_type := stage_array(i).data_type;
		
		if v_data_type = 'EQTL' then
			insert into biomart.bio_assay_analysis_eqtl
			(bio_asy_analysis_eqtl_id
			,bio_assay_analysis_id
			,rs_id
			,gene
			,p_value
			,p_value_char
			,cis_trans
			,distance_from_gene
			,etl_id
			,ext_data
			,log_p_value)
			select bio_asy_analysis_eqtl_id
				  ,bio_assay_analysis_id
				  ,rs_id
				  ,gene
				  ,to_binary_double(p_value_char)
				  ,p_value_char
				  ,cis_trans
				  ,distance_from_gene
				  ,etl_id
				  ,ext_data
				  ,log(10,to_binary_double(p_value_char))*-1
			from biomart_stage.bio_assay_analysis_eqtl
			where bio_assay_analysis_id = v_bio_assay_analysis_id;
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Insert data for analysis from BIOMART_STAGE.BIO_ASSAY_ANALYSIS_' || v_data_type,SQL%ROWCOUNT,stepCt,'Done');
			commit;		
		else
			insert into biomart.bio_assay_analysis_gwas
			(bio_asy_analysis_gwas_id
			,bio_assay_analysis_id
			,rs_id
			,p_value
			,p_value_char
			,etl_id
			,ext_data
			,log_p_value)
			select bio_asy_analysis_gwas_id
				  ,bio_assay_analysis_id
				  ,rs_id
				  ,to_binary_double(p_value_char)
				  ,p_value_char
				  ,etl_id
				  ,ext_data
				  ,log(10,to_binary_double(p_value_char))*-1
			from biomart_stage.bio_assay_analysis_gwas
			where bio_assay_analysis_id = v_bio_assay_analysis_id;
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Insert data for analysis from BIOMART_STAGE.BIO_ASSAY_ANALYSIS_' || v_data_type,SQL%ROWCOUNT,stepCt,'Done');
			commit;	
		end if;

		if i_etl_id > -1 then

			v_sqlText := 'delete from biomart_stage.bio_assay_analysis_' || v_data_type || 
						 ' where bio_assay_analysis_id = ' || to_char(v_bio_assay_analysis_id);
			--dbms_output.put_line(v_sqlText);
			execute immediate(v_sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Delete data for analysis from BIOMART_STAGE.BIO_ASSAY_ANALYSIS_' || v_data_type,SQL%ROWCOUNT,stepCt,'Done');
			commit;	
		end if;	
		
		update tm_lz.lz_src_analysis_metadata
		set status='PRODUCTION'
		where etl_id = v_etl_id;
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Set status to PRODUCTION in tm_lz.lz_src_analysis_metadata',SQL%ROWCOUNT,stepCt,'Done');
		commit;				
			
	end loop;
	
	if i_etl_id = -1 then
	
		select table_name
		bulk collect into stage_table_array
		from all_tables
		where owner = 'BIOMART_STAGE'
		  and table_name like 'BIO_ASSAY_ANALYSIS%';
		  
		for i in stage_table_array.first .. stage_table_array.last
		loop
			v_sqlText := 'truncate table biomart_stage.' || stage_table_array(i).table_name;
			--dbms_output.put_line(v_sqlText);
			execute immediate(v_sqlText);
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Truncated biomart_stage.' || stage_table_array(i).table_name,0,stepCt,'Done');
		end loop;
	end if;
	
	--	recreate GWAS indexes if needed and populate top 500 for GWAS
	
	if v_GWAS_staged = 1 then
		execute immediate('create index biomart.bio_assay_analysis_gwas_idx1 on biomart.bio_assay_analysis_gwas (bio_assay_analysis_id) tablespace "INDX"');
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Created index bio_assay_analysis_gwas_idx1',0,stepCt,'Done');
		execute immediate('create index biomart.bio_assay_analysis_gwas_idx2 on biomart.bio_assay_analysis_gwas (rs_id) tablespace "INDX"');
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Created index bio_assay_analysis_gwas_idx2',0,stepCt,'Done');
		execute immediate('create unique index biomart.bio_assay_analysis_gwas_pk on biomart.bio_assay_analysis_gwas (bio_asy_analysis_gwas_id) tablespace "INDX"');
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Created index bio_assay_analysis_gwas_pk',0,stepCt,'Done');
		
		--	populate top 500 table
		
		execute immediate('truncate table biomart.BIO_ASY_ANALYSIS_GWAS_TOP50');
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Truncated bio_asy_analysis_gwas_top50',0,stepCt,'Done');
		
		insert into biomart.bio_asy_analysis_gwas_top50
		(bio_assay_analysis_id
		,analysis
		,chrom
		,pos
		,rsgene
		,rsid
		,pvalue
		,logpvalue
		,extdata
		,rnum
		)
		select data.bio_assay_analysis_id
			  ,baa.analysis_name as analysis
			  ,info.chrom as chrom
			  ,info.pos as pos
			  ,gmap.gene_name as rsgene
			  ,data.rs_id as rsid
			  ,data.p_value as pvalue
			  ,data.log_p_value as logpvalue
			  ,data.ext_data as extdata
			  ,data.rnum
			from (select bio_asy_analysis_gwas_id
						,bio_assay_analysis_id
						,rs_id
						,p_value
						,log_p_value
						,ext_data
						,p_value_char
						,row_number () over (partition by bio_assay_analysis_id order by p_value asc, rs_id asc) as rnum
				   from biomart.bio_assay_analysis_gwas
				  ) data
			inner join biomart.bio_assay_analysis baa 
				  on  baa.bio_assay_analysis_id = data.bio_assay_analysis_id
			inner join deapp.de_rc_snp_info info 
				  on  data.rs_id = info.rs_id 
				  and info.hg_version='19'
			left outer join deapp.de_snp_gene_map gmap 
				 on  gmap.snp_name =info.rs_id
			where data.rnum <=500;
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Inserted data into bio_asy_analysis_gwas_top50',0,stepCt,'Done');	
	end if;
	
	if v_EQTL_staged = 1 then

		--	populate top 500 table
		
		execute immediate('truncate table biomart.BIO_ASY_ANALYSIS_EQTL_TOP50');
		stepCt := stepCt + 1;
		czx_write_audit(jobId,databaseName,procedureName,'Truncated bio_asy_analysis_eqtl_top50',0,stepCt,'Done');
		
		insert into biomart.bio_asy_analysis_eqtl_top50
		(bio_assay_analysis_id
		,analysis
		,chrom
		,pos
		,rsgene
		,rsid
		,pvalue
		,logpvalue
		,extdata
		,rnum
		)
		select data.bio_assay_analysis_id
			  ,baa.analysis_name as analysis
			  ,info.chrom as chrom
			  ,info.pos as pos
			  ,gmap.gene_name as rsgene
			  ,data.rs_id as rsid
			  ,data.p_value as pvalue
			  ,data.log_p_value as logpvalue
			  ,data.ext_data as extdata
			  ,data.rnum
			from (select bio_assay_analysis_id
						,rs_id
						,p_value
						,log_p_value
						,ext_data
						,p_value_char
						,row_number () over (partition by bio_assay_analysis_id order by p_value asc, rs_id asc) as rnum
				   from biomart.bio_assay_analysis_eqtl
				  ) data
			inner join biomart.bio_assay_analysis baa 
				  on  baa.bio_assay_analysis_id = data.bio_assay_analysis_id
			inner join deapp.de_rc_snp_info info 
				  on  data.rs_id = info.rs_id 
				  and info.hg_version='19'
			left outer join deapp.de_snp_gene_map gmap 
				 on  gmap.snp_name =info.rs_id
			where data.rnum <=500;
			stepCt := stepCt + 1;
			czx_write_audit(jobId,databaseName,procedureName,'Inserted data into bio_asy_analysis_eqtl_top50',0,stepCt,'Done');	
	end if;
	
	czx_write_audit(jobId,databaseName,procedureName,'End i2b2_move_analysis_to_prod',0,stepCt,'Done');
	stepCt := stepCt + 1;
	
	czx_end_audit(jobId, 'Success');
	
	exception
	when no_staged_data then
		czx_write_audit(jobId, databaseName, procedureName, 'No staged data - run terminating normally',0,stepCt,'Done');
		czx_end_audit(jobId, 'Success');
	when others then
    --Handle errors.
		czx_error_handler (jobID, procedureName);
    --End Proc
		czx_end_audit (jobID, 'FAIL');
	
END;
 
