CREATE PROCEDURE       "I2B2_LOAD_FROM_STAGE"
(
  trial_id IN VARCHAR2
 , data_type	In varchar2
 ,currentJobID NUMBER := null
 ,rtn_code	out number
 ) AUTHID CURRENT_USER
AS
/*************************************************************************
